   # 一時保存用のスコアに、倒したピンの数を追加する","    @temp << pins","    # 2投分のデータが入っているか、1投目がストライクだったら、1フレーム分のスコアとして全体に追加する","    if @temp.size == 2 || strike?(@temp)","      @scores << @temp","      @temp = []","    end","  end","","  # スコアの合計を計算する","  def calc_score","    @scores.each.with_index(1) do |score, index|","      # 最終フレーム以外でのストライクなら、スコアにボーナスを含めて合計する","      if strike?(score) && not_last_frame?(index)","        @total_score += calc_strike_bonus(index)","      # 最終フレーム以外でのスペアなら、スコアにボーナスを含めて合計する","      elsif spare?(score) && not_last_frame?(index)","        @total_score += calc_spare_bonus(index)","      else","        @total_score += score.inject(:+)","      end","      # 合計をフレームごとに記録しておく","      @frame_score << @total_score","    end","  end","","  private","  # スペアかどうか判定する","  def spare?(score)","    score.inject(:+) == 10","  end","","  # ストライ��かどうか判定する","  def strike?(score)","    score.first == 10","  end","","  # 最終フレーム以外かどうか判定する","  def not_last_frame?(index)","    index < 10","  end","","  # スペアボーナスを含んだ値でスコアを計算する","  def calc_spare_bonus(index)","    10 + @scores[index].first","  end","","  # ストライクボーナスを含んだ値でスコアを計算する","  def calc_strike_bonus(index)","    # 次のフレームもストライクで、なおかつ最終フレーム以外なら、もう一つ次のフレームの一投目をボーナスの対象にする","    if strike?(@scores[index]) && not_last_frame?(index)","      20 + @scores[index + 1].first","    else","      10 + @scores[index].inject(:+)","    end","  end","end"]}]]},"ace":{"folds":[],"scrolltop":480,"scrollleft":0,"selection":{"start":{"row":82,"column":3},"end":{"row":82,"column":3},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":11,"state":"start","mode":"ace/mode/ruby"}},"timestamp":1596413778687,"hash":"e200f02c80949b869a87b1654568416717b047b0"}