@game.total_score).to eq 0","      end","    end","","    context \"全ての投球で1ピンずつ倒した場合\" do","      it \"20になること\" do","        add_many_scores(20, 1)","        # 合計を計算","        @game.calc_score","        expect(@game.total_score).to eq 20","      end","    end","","    context \"スペアを取った場合\" do","      it \"スペアボーナスが加算されること\" do","        # 第一フレームで3点, 7点のスペア","        @game.add_score(3)","        @game.add_score(7)","        # 第二フレームの一投目で4点","        @game.add_score(4)","        # 以降は全てガター","        add_many_scores(17, 0)","        # 合計を計算","        @game.calc_score","        # 期待する合計　※()内はボーナス点","        # 3 + 7 + 4 + (4) = 18","        expect(@game.total_score).to eq 18","      end","    end","","    context \"最終フレームでスペアを取った場合\" do","      it \"スペアボーナスが加算されないこと\" do","        # 第一フレームで3点, 7点のスペア","        @game.add_score(3)","        @game.add_score(7)","        # 第二フレームの一投目で4点","        @game.add_score(4)","        # 15投は全てガター","        add_many_scores(15, 0)","        # 最終フレームで3点, 7点のスペア","        @game.add_score(3)","        @game.add_score(7)","        @game.add_score(4) #ここはボーナスが発生しない","        # 合計を計算","        @game.calc_score","        # 期待する合計　※()内はボーナス点","        # 3 + 7 + 4 + (4) + 3 + 7 + 4 = 32","        expect(@game.total_score).to eq 32","      end","    end","","    context \"フレーム違いでスペア�����なるようなスコアだった場合\" do","      it \"スペアボーナスが加算されないこと\" do","        # 第一フレームで3点, 5点","        @game.add_score(3)","        @game.add_score(5)","        # 第二フレームで5点, 4点","        @game.add_score(5)","        @game.add_score(4)","        # 以降は全てガター","        add_many_scores(16, 0)","        # 合計を計算","        @game.calc_score","        # 期待する合計","        # 3 + 5 + 5 + 4 = 17","        expect(@game.total_score).to eq 17","      end","    end","","    context \"ストライクを取った場合\" do","      it \"ストライクボーナスが加算されること\" do","        # 第一フレームでストライク","        @game.add_score(10)","        # 第二フレームで5点, 4点","        @game.add_score(5)","        @game.add_score(4)","        # 以降は全てガター","        add_many_scores(16, 0)","        # 合計を計算","        @game.calc_score","        # 期待する合計　※()内はボーナス点","        # 10 + 5 + (5) + 4 + (4) = 28","        expect(@game.total_score).to eq 28","      end","    end","","    context \"ダブルを取った場合\" do","      it \"それぞれのストライクボーナスが加算されること\" do","        # 第一フレームでストライク","        @game.add_score(10)","        # 第二フレームもストライク","        @game.add_score(10)","        # 第三フレームで5点, 4点","        @game.add_score(5)","        @game.add_score(4)","        # 以降は全てガター","        add_many_scores(14, 0)","        # 合計を計算","        @game.calc_score","        # 期待する合計　※()内はボーナス点","        # 10 + 10 + (10) + 5 +(5 + 5) + 4 + (4) = 53","        expect(@game.total_score).to eq 53","      end","    end","","    context \"ターキーを取った場合\" do","      it \"それぞれのストライクボーナスが加算されること\" do","        # 第一フレームでストライク","        @game.add_score(10)","        # 第二フレームもストライク","        @game.add_score(10)","        # 第三フレームもストライク","        @game.add_score(10)","        # 第四フレームで5点, 4点","        @game.add_score(5)","        @game.add_score(4)","        # 以降は全てガター","        add_many_scores(12, 0)","        # 合計を計算","        @game.calc_score","        # 期待する合計　※()内はボーナス点","        # 10 + 10 + (10) + 10 + (10 + 10) + 5 +(5 + 5) + 4 + (4) = 83","        expect(@game.total_score).to eq 83","      end","    end","","    context \"最終フレームでストライクを取った場合\" do","      it \"ストライクボーナスが加算されないこと\" do","        # ��一��レームでストライク","        @game.add_score(10)","        # 第二フレームで5点, 4点","        @game.add_score(5)","        @game.add_score(4)","        # 3~9フレームは全てガター","        add_many_scores(14, 0)","        # 最終フレームでストライク","        @game.add_score(10)","        @game.add_score(4)","        @game.add_score(5)","        # 合計を計算","        @game.calc_score","        # 期待する合計　※()内はボーナス点","        # 10 + 5 + (5) + 4 + (4) + 10 + 4 + 5= 47","        expect(@game.total_score).to eq 47","      end","    end","  end","  ","  describe \"フレームごとの合計\" do","    context \"全ての投球で1ピンずつ倒した場合\" do","      it \"1フレーム目の合計が2になること\" do","        add_many_scores(20, 1)","        # 合計を計算","        @game.calc_score","        expect(@game.frame_score(1)).to eq 2","      end","    end","","    context \"スペアを取った場合\" do","      it \"スペアボーナスが加算されること\" do","        # 第一フレームで3点, 7点のスペア","        @game.add_score(3)","        @game.add_score(7)","        # 第二フレームの一投目で4点","        @game.add_score(4)","        # 以降は全てガター","        add_many_scores(17, 0)","        # 合計を計算","        @game.calc_score","        # 期待する合計　※()内はボーナス点","        # 3 + 7 + (4) = 14","        expect(@game.frame_score(1)).to eq 14","      end","    end","","    context \"ストライクを取った場合\" do","      it \"ストライクボーナスが加算されること\" do","        # 第一フレームでストライク","        @game.add_score(10)","        # 第二フレームで5点, 4点","        @game.add_score(5)","        @game.add_score(4)","        # 以降は全てガター","        add_many_scores(16, 0)","        # 合計を計算","        @game.calc_score","        # 期待する合計　※()内はボーナス点","        # 10 + (5) + (4) = 19","        expect(@game.frame_score(1)).to eq 19","      end","    end","  end","end","","private","# 複数回のスコア追加をまとめて実行する","def add_many_scores(count, pins)","  count.times do","    @game.add_score(pins)","  end","end"]}],[{"start":{"row":50,"column":24},"end":{"row":50,"column":25},"action":"remove","lines":["4"],"id":95}],[{"start":{"row":50,"column":24},"end":{"row":50,"column":25},"action":"insert","lines":["6"],"id":96}],[{"start":{"row":50,"column":24},"end":{"row":50,"column":25},"action":"remove","lines":["6"],"id":97}],[{"start":{"row":50,"column":24},"end":{"row":50,"column":25},"action":"insert","lines":["4"],"id":98}],[{"start":{"row":50,"column":24},"end":{"row":50,"column":25},"action":"remove","lines":["4"],"id":99}],[{"start":{"row":50,"column":24},"end":{"row":50,"column":25},"action":"insert","lines":["8"],"id":100}],[{"start":{"row":50,"column":24},"end":{"row":50,"column":25},"action":"remove","lines":["8"],"id":101}],[{"start":{"row":50,"column":24},"end":{"row":50,"column":25},"